// Toggle the and/or behavior of the search, console logging is for consistency with past versions
function andOrToggle(buttonId) {
    document.getElementById(buttonId.id).disabled = true;
    if (buttonId.id == 'AndButton') {
        functionality = 'and';
        document.getElementById('OrButton').disabled = false; 
        document.getElementById('NotButton').disabled = false;
        refreshTableData();           
    } else if (buttonId.id == 'OrButton') {
        functionality = 'or';
        document.getElementById('AndButton').disabled = false;
        document.getElementById('NotButton').disabled = false;
        refreshTableData();
    } else {
        functionality = 'not';
        document.getElementById('AndButton').disabled = false;
        document.getElementById('OrButton').disabled = false;
        refreshTableData();
    }
}

